fun findFinalFloor(input: String): Int {
    val up = input.count { it == '(' }
    val down = input.count { it == ')' }
    return up - down
}

fun findBasementPosition(input: String): Int {
    var current: Int = 0
    input.forEachIndexed { index, c ->
        if (c == '(') current++
        else current--
        if(current < 0) return@findBasementPosition index+1
    }
    return -1
}
